INSERT INTO `wp_services` (`id`, `key`, `title`, `icon_offline`, `icon_online`, `list_icon`, `list_description`, `tutorial_url`, `category`) VALUES
(1, 'my_website', 'My Website', 'website_inactive_icon.png', 'website_icon.png', 'icon_website.png', 'Add and manage your own website with the dashboard or if you have not got a website, you might be interested in viewing a tutorial on how to build one.', 'http://www.thesitewizard.com/gettingstarted/startwebsite.shtml', 'market'),
(2, 'google_places', 'Google places', 'googleplaces_inactive_icon.png', 'googleplaces_icon.png', 'icon_googleplaces.png', 'Google Places gets your business on Google Search, Maps, Google+ and mobile. This service is very useful for allowing customers to find you.', 'http://www.youtube.com/watch?v=S3v_EVhfPAA', 'market'),
(3, 'facebook', 'Facebook', 'facebook_inactive_icon.png', 'facebook_icon.png', 'icon_facebook.png', 'Facebook is a very popular and well used website. It allows people to create profiles and communicate with each other.', 'https://www.facebook.com/', 'communicating'),
(4, 'twitter ', 'Twitter', 'twitter_inactive_icon.png', 'twitter_icon.png', 'icon_twitter.png', 'Twitter allows fast communication with your audience through 140 characters.', 'http://www.youtube.com/watch?v=gcjvIgGGMnA', 'communicating'),
(5, 'yell', 'Yell', 'yell_inactive_icon.png', 'yell_icon.png', 'icon_yell.png', 'Yell is a popular online business directory, allowing customers to search and find you through the Yell search engine.', 'http://www.yell.com/help/yourbusiness/optimise1.html', 'market'),
(6, 'trip_advisor', 'Trip Advisor', 'tripadvisor_inactive_icon.png', 'tripadvisor_icon.png', 'icon_tripadvisor.png', 'Trip Advisor is a very popular service, which lists businesses by customer reviews and location. It allows customers to find, review and recommend your businesses.', 'http://www.tripadvisor.co.uk/Owners', 'market'),
(7, 'youtube', 'Youtube', 'youtube_inactive_icon.png', 'youtube_icon.png', 'icon_youtube.png', 'YouTube is the most popular video sharing service on the Internet, allowing you to upload and share videos within communities and people in the world.', 'http://www.teachertrainingvideos.com/youTube/', 'multimedia'),
(8, 'instagram', 'Instagram', 'instagram_inactive_icon.png', 'instagram_icon.png', 'icon_instagram.png', 'Instagram is a mobile application for taking and sharing photos. It is also a social network allowing you to create photo communities.', 'http://www.wikihow.com/Use-Instagram', 'multimedia'),
(9, 'flickr', 'Flickr', 'flickr_inactive_icon.png', 'flickr_icon.png', 'icon_flickr.png', 'Flickr is an online photo management and photo sharing service. It also allows you to build an online photo sharing community.', 'http://www.youtube.com/watch?v=dVeZhYhmLzc', 'multimedia'),
(10, 'ebay', 'Ebay', 'ebay_inactive_icon.png', 'ebay_icon.png', 'icon_ebay.png', 'Ebay is an e-commerce website, allowing you to sell and buy items online.', 'http://pages.ebay.com/help/account/gettingstarted.html', 'sell'),
(11, 'etsy', 'Etsy', 'etsy_inactive_icon.png', 'etsy_icon.png', 'icon_etsy.png', 'Etsy is an e-commerce website, allowing you to sell and buy hand-made and vintage items online.', 'http://www.grovo.com/etsy', 'sell'),
(12, 'paypal', 'Paypal', 'paypal_inactive_icon.png', 'paypal_icon.png', 'icon_paypal.png', 'Paypal is a quick and safe way of sending and receive money online.', 'http://www.youtube.com/watch?v=HSQ7dl8Zgrk', 'payment'),
(13, 'collect', 'Collect+', 'collectplus_inactive_icon.png', 'collectplus_icon.png', 'icon_collectplus.png', 'Parcels made easy. Send, collect and return parcels at your local corner shop, from early ''til late, 7 days a week.', 'http://www.collectplus.co.uk', 'packages'),
(14, 'email', 'Email', 'email_inactive_icon.png', 'email_icon.png', 'icon_email.png', 'Email is the most popular way of sending electronic messages on the Internet. It give you a way of being reached and allows you to get in contact with businesses that are online.', 'http://dawn.thot.net/cd/1.html', 'communicating'),
(15, 'google_analytics', 'Google Analytics', 'googleanalytics_inactive_icon.png', 'googleanalytics_icon.png', 'icon_googleanalytics.png', 'Google Analytics allows you to find out who looks at your web pages.', 'http://www.youtube.com/watch?v=mm78xlsADgc', 'who'),
(16, 'credly', 'Credly', 'credly_inactive_icon.png', 'credly_icon.png', 'icon_credly.png', 'Credly offers a service for verifying, sharing and managing digital badges and credentials.', 'https://credly.com', 'business_community'),
(17, 'customer_base', 'Customer Base', 'customerbase_inactive_icon.png', 'customerbase_icon.png', 'icon_customerbase.png', 'Customer base allows you to find out about your customers and who visits your online web pages. It gives you a statistical analysis of your customers for each web service that you use.', 'http://www.google.com', 'who'),
(18, 'facebook_page', 'Facebook Page', 'facebook_page_inactive_icon.png', 'facebook_page_icon.png', 'icon_facebook_page.png', 'A Facebook Page is for a business, organisation or brand to share their stories and connect with people. ', 'http://www.facebook.com/pages', 'communicating');
